import React, { useState } from "react";
import "../Styles/BusTicketManagement.css";
import { Ticket } from "../model/Ticket";

const BusTicketManagement: React.FC = () => {
    const [tickets, setTickets] = useState<Ticket[]>([
        {
            ticketId: 1,
            passengerName: "John Doe",
            travelDate: "2023-10-01",
            departure: "City A",
            destination: "City B",
            price: 50,
        },
        {
            ticketId: 2,
            passengerName: "Jane Smith",
            travelDate: "2023-10-02",
            departure: "City C",
            destination: "City D",
            price: 60,
        },
    ]);

    const [isFormVisible, setIsFormVisible] = useState(false);
    const [formMode, setFormMode] = useState<"add" | "edit">("add");
    const [currentTicket, setCurrentTicket] = useState<Ticket | null>(null);
    const [showHistory, setShowHistory] = useState(false);
    const [searchQuery, setSearchQuery] = useState("");
    const [filterDate, setFilterDate] = useState(() => {
        const today = new Date();
        return `${today.getFullYear()}-${String(today.getMonth() + 1).padStart(2, "0")}`;
    });

    const handleAdd = () => {
        setFormMode("add");
        setCurrentTicket(null);
        setIsFormVisible(true);
    };

    const handleEdit = (ticket: Ticket) => {
        setFormMode("edit");
        setCurrentTicket(ticket);
        setIsFormVisible(true);
    };

    const handleDelete = (ticketId: number) => {
        setTickets(tickets.filter((ticket) => ticket.ticketId !== ticketId));
    };

    const handleFormSubmit = (ticket: Ticket) => {
        if (formMode === "add") {
            setTickets([...tickets, { ...ticket, ticketId: tickets.length + 1 }]);
        } else if (formMode === "edit" && currentTicket) {
            setTickets(
                tickets.map((t) =>
                    t.ticketId === currentTicket.ticketId ? ticket : t
                )
            );
        }
        setIsFormVisible(false);
    };

    const handleViewHistory = () => {
        setShowHistory(!showHistory);
    };

    const today = new Date().toISOString().split("T")[0];
    const pastTickets = tickets.filter((ticket) => ticket.travelDate < today);

    const filteredTickets = pastTickets.filter((ticket) => {
        const matchesSearch = ticket.passengerName
            .toLowerCase()
            .includes(searchQuery.toLowerCase());
        const matchesDate = filterDate
            ? ticket.travelDate.startsWith(filterDate)
            : true;
        return matchesSearch && matchesDate;
    });

    return (
        <div className="bus-ticket-management">
            <h2>Danh sách vé xe bus</h2>
            <button onClick={handleViewHistory} className="history-button">
                {showHistory ? "Ẩn lịch sử" : "Xem lịch sử vé đã đặt"}
            </button>
            {!showHistory && (
                <>
                    <button onClick={handleAdd} className="add-button">Thêm vé</button>
                    <table>
                        <thead>
                            <tr>
                                <th>Mã vé</th>
                                <th>Tên hành khách</th>
                                <th>Ngày đi</th>
                                <th>Điểm khởi hành</th>
                                <th>Điểm đến</th>
                                <th>Giá vé</th>
                                <th>Hành động</th>
                            </tr>
                        </thead>
                        <tbody>
                            {tickets.map((ticket) => (
                                <tr key={ticket.ticketId}>
                                    <td>{ticket.ticketId}</td>
                                    <td>{ticket.passengerName}</td>
                                    <td>{ticket.travelDate}</td>
                                    <td>{ticket.departure}</td>
                                    <td>{ticket.destination}</td>
                                    <td>{ticket.price}</td>
                                    <td>
                                        <button
                                            onClick={() => handleEdit(ticket)}
                                            className="edit-button"
                                        >
                                            Sửa
                                        </button>
                                        <button
                                            onClick={() => handleDelete(ticket.ticketId)}
                                            className="delete-button"
                                        >
                                            Xóa
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </>
            )}
            {showHistory && (
                <div className="ticket-history">
                    <h3>Lịch sử vé đã đặt</h3>
                    <div className="history-filters">
                        <input
                            type="text"
                            placeholder="Tìm kiếm theo tên hành khách"
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                        />
                        <input
                            type="month"
                            value={filterDate}
                            onChange={(e) => setFilterDate(e.target.value)}
                        />
                    </div>
                    <table>
                        <thead>
                            <tr>
                                <th>Mã vé</th>
                                <th>Tên hành khách</th>
                                <th>Ngày đi</th>
                                <th>Điểm khởi hành</th>
                                <th>Điểm đến</th>
                                <th>Giá vé</th>
                            </tr>
                        </thead>
                        <tbody>
                            {filteredTickets.map((ticket) => (
                                <tr key={ticket.ticketId}>
                                    <td>{ticket.ticketId}</td>
                                    <td>{ticket.passengerName}</td>
                                    <td>{ticket.travelDate}</td>
                                    <td>{ticket.departure}</td>
                                    <td>{ticket.destination}</td>
                                    <td>{ticket.price}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
            {isFormVisible && (
                <TicketForm
                    mode={formMode}
                    ticket={currentTicket}
                    onSubmit={handleFormSubmit}
                    onCancel={() => setIsFormVisible(false)}
                />
            )}
        </div>
    );
};

interface TicketFormProps {
    mode: "add" | "edit";
    ticket: Ticket | null;
    onSubmit: (ticket: Ticket) => void;
    onCancel: () => void;
}

const TicketForm: React.FC<TicketFormProps> = ({ mode, ticket, onSubmit, onCancel }) => {
    const [formData, setFormData] = useState<Ticket>(
        ticket || {
            ticketId: 0,
            passengerName: "",
            travelDate: "",
            departure: "",
            destination: "",
            price: 0,
        }
    );

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: name === "price" ? +value : value });
    };

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        onSubmit(formData);
    };

    return (
        <div className="ticket-form-overlay">
            <div className="ticket-form">
                <h3>{mode === "add" ? "Thêm vé mới" : "Chỉnh sửa vé"}</h3>
                <form onSubmit={handleSubmit}>
                    <label>
                        Tên hành khách:
                        <input
                            type="text"
                            name="passengerName"
                            value={formData.passengerName}
                            onChange={handleChange}
                            required
                        />
                    </label>
                    <label>
                        Ngày đi:
                        <input
                            type="date"
                            name="travelDate"
                            value={formData.travelDate}
                            onChange={handleChange}
                            required
                        />
                    </label>
                    <label>
                        Điểm khởi hành:
                        <input
                            type="text"
                            name="departure"
                            value={formData.departure}
                            onChange={handleChange}
                            required
                        />
                    </label>
                    <label>
                        Điểm đến:
                        <input
                            type="text"
                            name="destination"
                            value={formData.destination}
                            onChange={handleChange}
                            required
                        />
                    </label>
                    <label>
                        Giá vé:
                        <input
                            type="number"
                            name="price"
                            value={formData.price}
                            onChange={handleChange}
                            required
                        />
                    </label>
                    <div className="form-actions">
                        <button type="submit">Lưu</button>
                        <button type="button" onClick={onCancel}>
                            Hủy
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default BusTicketManagement;
