import { useState } from "react";
import "./auth_css/SignIn_SignUp.css";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export function ForgotPassword() {
  const [email, setEmail] = useState("");
  const navigate = useNavigate();

  const handleForgotPassword = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://127.0.0.1:8080/auth/forgot-password", {
        email: email,
      });

      if (response.status === 200) {
        alert("Password reset link has been sent to your email.");
        navigate("/signin"); // Quay lại trang Sign In sau khi gửi thành công
      }
    } catch (error: any) {
      console.error("Error:", error.response?.data?.message || error.message);
      alert(`Failed to send reset link: ${error.response?.data?.message || "Unknown error"}`);
    }
  };

  return (
    <form onSubmit={handleForgotPassword}>
      <h1>Forgot Password</h1>
      <input
        type="email"
        placeholder="Enter your email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
      />
      <button type="submit">Send Reset Link</button>
      <a href="#" onClick={() => navigate("/signin")}>
        Back to Sign In
      </a>
    </form>
  );
}
