import React, { useState } from "react";
import Navbar from "../../Components/navbar/Navbar";
import Footer from "../../Components/common/Footer";
import "./home_css/home.css";

const Home: React.FC<{ setCurrentPage: (page: string) => void }> = ({ setCurrentPage }) => {
  const [from, setFrom] = useState("");
  const [to, setTo] = useState("");
  const [date, setDate] = useState("");

  const ticketData = [
    { route: "Đà Nẵng → Hà Nội", price: "350,000 VND", seats: "Còn 5 chỗ" },
    { route: "TP.HCM → Đà Nẵng", price: "450,000 VND", seats: "Còn 10 chỗ" },
    { route: "Hà Nội → Sapa", price: "300,000 VND", seats: "Còn 2 chỗ" },
  ];

  const vehicleTypes = [
    { name: "Xe bus", image: "bus.jpg" },
    { name: "Máy bay", image: "plane.jpg" },
    { name: "Xe khách", image: "coach.jpg" },
    { name: "Xe taxi", image: "taxi.jpg" },
    { name: "Xe du lịch", image: "tourist.jpg" },
  ];

  // Hàm xử lý khi nhấn nút "Mua vé"
  const handleBuyTicket = (route: string) => {
    alert(`Bạn đã chọn mua vé cho tuyến: ${route}`);
    // Thêm logic xử lý mua vé tại đây (ví dụ: chuyển hướng đến trang thanh toán)
  };

  return (
    <div className="home-page">
      <div className="home">
        {/* Navigation */}
        <Navbar setCurrentPage={setCurrentPage} />

        {/* Header */}
        <header className="header">
          <h1>Đặt vé xe khách trực tuyến</h1>
          <div className="search-bar">
            <input
              type="text"
              placeholder="Nơi đi"
              value={from}
              onChange={(e) => setFrom(e.target.value)}
            />
            <input
              type="text"
              placeholder="Nơi đến"
              value={to}
              onChange={(e) => setTo(e.target.value)}
            />
            <input
              type="date"
              placeholder="Ngày khởi hành"
              value={date}
              onChange={(e) => setDate(e.target.value)}
            />
            <button>Tìm vé</button>
          </div>
        </header>

        {/* Lựa chọn phương tiện */}
        <section className="vehicle-selection">
          <h2>Mua vé xe gì?</h2>
          <div className="vehicle-cards">
            {vehicleTypes.map((vehicle, index) => (
              <div key={index} className="vehicle-card">
                <img src={vehicle.image} alt={vehicle.name} />
                <h3>{vehicle.name}</h3>
              </div>
            ))}
          </div>
        </section>

        {/* Danh sách vé */}
        <section className="ticket-list">
          <h2>Danh sách vé xe</h2>
          <div className="tickets">
            {ticketData.map((ticket, index) => (
              <div key={index} className="ticket-card">
                <p>
                  <strong>{ticket.route}</strong>
                </p>
                <p>Giá vé: {ticket.price}</p>
                <p>{ticket.seats}</p>
                <button onClick={() => handleBuyTicket(ticket.route)}>Mua vé</button>
              </div>
            ))}
          </div>
        </section>

        {/* Footer */}
        <Footer year={2025} companyName="Ticket Car" />
      </div>
    </div>
  );
};

export default Home;
