import React, { useState } from "react";
import Navbar from "../../Components/navbar/Navbar";
import Footer from "../../Components/common/Footer";
import "./bus_ticket_css/bus_ticket.css";

const BusTicket: React.FC<{ setCurrentPage: (page: string) => void }> = ({ setCurrentPage }) => {
  const [from, setFrom] = useState("");
  const [to, setTo] = useState("");
  const [date, setDate] = useState("");

  const trips = [
    {
      id: 1,
      route: "Hà Nội → Hồ Chí Minh",
      price: "500,000 VND",
      seats: "Còn 10 chỗ",
      dateTime: "2025-04-15 08:00",
      rating: 4.5,
      duration: "30 giờ",
    },
    {
      id: 2,
      route: "Hà Nội → Đà Nẵng",
      price: "350,000 VND",
      seats: "Còn 5 chỗ",
      dateTime: "2025-04-16 07:00",
      rating: 4.2,
      duration: "15 giờ",
    },
    {
      id: 3,
      route: "Hà Nội → Đà Lạt",
      price: "450,000 VND",
      seats: "Còn 8 chỗ",
      dateTime: "2025-04-17 06:30",
      rating: 4.8,
      duration: "25 giờ",
    },
  ];

  // Lọc danh sách chuyến đi dựa trên thông tin tìm kiếm
  const filteredTrips = trips.filter((trip) => {
    const [tripFrom, tripTo] = trip.route.split(" → ");
    const isFromMatch = from === "" || tripFrom.toLowerCase().includes(from.toLowerCase());
    const isToMatch = to === "" || tripTo.toLowerCase().includes(to.toLowerCase());
    const isDateMatch = date === "" || trip.dateTime.startsWith(date);
    return isFromMatch && isToMatch && isDateMatch;
  });

  const handleSearch = () => {
    if (filteredTrips.length === 0) {
      alert("Không tìm thấy chuyến đi phù hợp.");
    }
  };

  return (
    <div className="bus-ticket-page">
      <Navbar setCurrentPage={setCurrentPage} />
      <header className="bus-ticket-header">
        <h1>Mua vé xe bus</h1>
        {/* Thanh tìm kiếm */}
        <div className="search-bar">
          <input
            type="text"
            placeholder="Nơi đi"
            value={from}
            onChange={(e) => setFrom(e.target.value)}
          />
          <input
            type="text"
            placeholder="Nơi đến"
            value={to}
            onChange={(e) => setTo(e.target.value)}
          />
          <input
            type="date"
            placeholder="Ngày khởi hành"
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />
          <button onClick={handleSearch}>Tìm vé</button>
        </div>
      </header>
      <div className="ticket-cards">
        {filteredTrips.map((trip) => (
          <div key={trip.id} className="ticket-card">
            <p>
              <strong>{trip.route}</strong>
            </p>
            <p>Giá vé: {trip.price}</p>
            <p>Số chỗ: {trip.seats}</p>
            <p>Ngày giờ: {trip.dateTime}</p>
            <p>Thời gian đi: {trip.duration}</p>
            <p>Đánh giá: {trip.rating} ★</p>
            <button>Mua vé</button>
          </div>
        ))}
        {filteredTrips.length === 0 && <p>Không có chuyến đi nào phù hợp.</p>}
      </div>
      <Footer year={2025} companyName="Ticket Car" />
    </div>
  );
};

export default BusTicket;
