import React, { useState } from "react";
import Navbar from "../../Components/navbar/Navbar";
import "./home_css/home.css";

const Home: React.FC<{ setCurrentPage: (page: string) => void }> = ({ setCurrentPage }) => {
  const [from, setFrom] = useState("");
  const [to, setTo] = useState("");
  const [date, setDate] = useState("");

  const ticketData = [
    { route: "Đà Nẵng → Hà Nội", price: "350,000 VND", seats: "Còn 5 chỗ" },
    { route: "TP.HCM → Đà Nẵng", price: "450,000 VND", seats: "Còn 10 chỗ" },
    { route: "Hà Nội → Sapa", price: "300,000 VND", seats: "Còn 2 chỗ" },
  ];

  return (
    <div className="home-page">
      <div className="home">
        {/* Navigation */}
        <Navbar setCurrentPage={setCurrentPage} />

        {/* Header */}
        <header className="header">
          <h1>Đặt vé xe khách trực tuyến</h1>
          <div className="search-bar">
            <input
              type="text"
              placeholder="Nơi đi"
              value={from}
              onChange={(e) => setFrom(e.target.value)}
            />
            <input
              type="text"
              placeholder="Nơi đến"
              value={to}
              onChange={(e) => setTo(e.target.value)}
            />
            <input
              type="date"
              placeholder="Ngày khởi hành"
              value={date}
              onChange={(e) => setDate(e.target.value)}
            />
            <button>Tìm vé</button>
          </div>
        </header>

        {/* Danh sách vé */}
        <section className="ticket-list">
          <h2>Danh sách vé xe</h2>
          <div className="tickets">
            {ticketData.map((ticket, index) => (
              <div key={index} className="ticket-card">
                <p>
                  <strong>{ticket.route}</strong>
                </p>
                <p>Giá vé: {ticket.price}</p>
                <p>{ticket.seats}</p>
                <button>Đặt vé</button>
              </div>
            ))}
          </div>
        </section>

        {/* Footer */}
        <footer className="footer">
          <p>© Ticket Car - Đặt vé xe khách nhanh chóng & tiện lợi.</p>
        </footer>
      </div>
    </div>
  );
};

export default Home;
