import React, { useState } from "react";
import Home from "./Components/home/home";
import TicketManagement from "./Components/TicketManagement";
import AuthContainer from "./Auth/AuthContainer";
import Payment from "./Components/payment/Payment";

const App: React.FC = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [currentPage, setCurrentPage] = useState("home");

  const handleLoginSuccess = () => {
    setIsAuthenticated(true);
    setCurrentPage("ticketManagement");
  };

  const renderPage = () => {
    switch (currentPage) {
      case "home":
        return <Home setCurrentPage={setCurrentPage} />; // Truyền `setCurrentPage` vào đây
      case "auth":
        return <AuthContainer onAuthSuccess={handleLoginSuccess} />;
      case "ticketManagement":
        return isAuthenticated ? <TicketManagement /> : <AuthContainer onAuthSuccess={handleLoginSuccess} />;
      case "payment":
        return <Payment />;
      default:
        return <Home setCurrentPage={setCurrentPage} />;
    }
  };

  return <div>{renderPage()}</div>;
};

export default App;
