import React from "react";
import { FaFacebook, FaTwitter, FaInstagram, FaPhone, FaEnvelope, FaMapMarkerAlt } from "react-icons/fa"; // Import các icon

// Define props type
interface FooterProps {
  year: number;
  companyName: string;
}

const Footer: React.FC<FooterProps> = ({ year, companyName }) => {
  return (
    <footer className="footer">
      <div className="footer-content">
        {/* Logo */}
        <div className="footer-logo">
          <img src="/logo.png" alt={`${companyName} Logo`} />
        </div>

        {/* Thông tin công ty */}
        <div className="footer-info">
          <p>© {year} {companyName} - Đặt vé xe khách nhanh chóng & tiện lợi.</p>
          <p>
            <FaMapMarkerAlt /> Địa chỉ: 123 Đường ABC, Quận XYZ, Thành phố Hồ Chí Minh
          </p>
          <p>
            <FaEnvelope /> Email: support@{companyName.toLowerCase().replace(/\s+/g, "")}.com
          </p>
          <p>
            <FaPhone /> Hotline: 1900 123 456
          </p>
        </div>

        {/* Liên kết mạng xã hội */}
        <div className="footer-social">
          <a href="https://facebook.com" target="_blank" rel="noopener noreferrer">
            <FaFacebook /> Facebook
          </a>
          <a href="https://twitter.com" target="_blank" rel="noopener noreferrer">
            <FaTwitter /> Twitter
          </a>
          <a href="https://instagram.com" target="_blank" rel="noopener noreferrer">
            <FaInstagram /> Instagram
          </a>
        </div>

        {/* Liên kết bổ sung */}
        <div className="footer-links">
          <a href="/privacy-policy">Chính sách bảo mật</a>
          <a href="/terms-of-service">Điều khoản sử dụng</a>
          <a href="/faq">Câu hỏi thường gặp</a>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
